import React, { useEffect, useState } from 'react';
import Pusher from 'pusher-js';

// Definición de la interfaz para mensajes
interface Message {
  username: string;
  message: string;
}

const App: React.FC = () => {
  const [username, setUserName] = useState<string>('username');
  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState<string>("");

  useEffect(() => {
    let pusher: Pusher;

    const connectPusher = () => {
      pusher = new Pusher('efc5c18caf9f89f8e460', {
        cluster: 'mt1',
        forceTLS: true,
        // logToConsole: true, // Habilitar logging para depuración
      });

      const channel = pusher.subscribe('banana-666');

      channel.bind('chat-event', (data: Message) => {
        setMessages(prevMessages => [...prevMessages, data]);
      });

      pusher.connection.bind('disconnected', () => {
        console.log('Disconnected. Attempting to reconnect...');
        setTimeout(() => connectPusher(), 3000); // Espera 3 segundos antes de reconectar
      });
    };

    connectPusher();

    return () => {
      if (pusher) {
        pusher.disconnect();
      }
    }
  }, []);


  const submit = async (e: React.FormEvent) => {
    e.preventDefault(); // Prevenir la recarga de la página al enviar el formulario
    await fetch('http://localhost:8000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        username: username,
        message: message
      })
    });
    setMessage('');
  }

  return (
    <div className="App">
      <div className='container'>
        <div className="list-group-item list-group-item-action active py-3 lh-sm" aria-current="true">
          <div className="d-flex w-100 align-items-center justify-content-between">
            <strong className="mb-1">List group</strong>
          </div>
          <input
            className='fs-5 fw-semibold'
            value={username}
            onChange={e => setUserName(e.target.value)}
          />
        </div>

        <div className="list-group list-group-flush border-bottom scrollarea">
          {messages.map((message, index) => (
            <div key={index} className="list-group-item list-group-item-action py-3 lh-sm">
              <div className="d-flex w-100 align-items-center justify-content-between">
                <strong className="mb-1" style={{ color: "black" }}>{message.username}</strong>
              </div>
              <div className="col-10 mb-1 small">{message.message}</div>
            </div>
          ))}
        </div>
      </div>
      <form onSubmit={submit}>
        <input
          className='form-control'
          placeholder='Write a message'
          value={message}
          onChange={e => setMessage(e.target.value)}
        />
      </form>
    </div>
  );
}

export default App;
