import React, { useEffect, useState } from 'react';
import './App.css';
import Pusher from 'pusher-js';

interface Message {
  username: string;
  message: string;
}

function App() {
  const [username, setUserName] = useState('username');
  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const pusher = new Pusher('efc5c18caf9f89f8e460', {
      cluster: 'mt1',
      forceTLS: true,
      reconnectAttempts: 3, // Intentar reconectar 3 veces antes de dar error
      reconnectDelay: 1000 // 1 segundo entre intentos de reconexión
    });

    const channel = pusher.subscribe('banana-666');

    channel.bind('chat-event', (data) => {
      setMessages(prevMessages => [...prevMessages, data]);
    });

    pusher.connection.bind('state_change', (states) => {
      console.log(`Connection state changed from ${states.previous} to ${states.current}`);
    });

    return () => {
      channel.unbind_all();
      channel.unsubscribe();
      pusher.disconnect();
    }
  }, []);


  const somit = async (e: React.FormEvent) => {
    e.preventDefault();
    await fetch('http://localhost:8000/api/messages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        username: username,
        message: message
      })
    });
    setMessage('');
  }

  return (
    <div className="App">
      <div className='container'>
        <div className="list-group-item list-group-item-action active py-3 lh-sm" aria-current="true">
          <div className="d-flex w-100 align-items-center justify-content-between">
            <strong className="mb-1">List group</strong>
          </div>
          <input className='fs-5 fw-semibold' value={username}
            onChange={e => setUserName(e.target.value)} />
        </div>

        <div className="list-group list-group-flush border-bottom scrollarea">
          {messages.map((message, index) => (
            <div key={index} className="list-group-item list-group-item-action py-3 lh-sm">
              <div className="d-flex w-100 align-items-center justify-content-between">
                <strong className="mb-1" style={{ color: "black" }}>{message.username}</strong>
              </div>
              <div className="col-10 mb-1 small">{message.message}</div>
            </div>
          ))}
        </div>
      </div>
      <form onSubmit={somit}>
        <input className='form-control' placeholder='Write a message' value={message}
          onChange={e => setMessage(e.target.value)}
        />
      </form>
    </div>
  );
}

export default App;
